package collection;

import java.util.ArrayList;

public class RemoveElement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		

		ArrayList l2= new ArrayList();
		l2.add("Pen");
		l2.add("1234");
		l2.add("Orange");
		l2.add("ABCC");
	
		System.out.println("Removing third element " +l2.remove(2));
		System.out.println("Collection after removing third element " +l2);
		
		

	}

}
