package excersises;

public class MethodStaticEg {

	public static int sum(int num1, int num2)
	{
		int s=num1+num2;
		return s;
		
	}
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	int	s1=sum(2,3);
		System.out.println("Sum of two numbers " +s1);

	}

}
